codeExamples.code:
// Fichier : main.c
int main(void)
{
	printf("Hello\n");  // ❌ Erreur !
	return (0);
}

// Erreur de compilation :
// error: implicit declaration of function 'printf'

#include <stdio.h>

int main(void)
{
	printf("Hello\n")  // ❌ Oubli du ;
	return (0);
}

// Erreur :
// error: expected ';' before 'return'

#include <stdio.h>

int main(void)
{
	printf("Hello);  // ❌ Guillemet fermant manquant
	return (0);
}

// Erreur :
// error: missing terminating " character

#include <stdio.h>
int main(void) {
    printf("Hello\n");  // Espaces au lieu de TAB
    return (0); }
// Accolades mal placées

printf("Ligne 1
");
printf("Ligne 2
");
printf("Ligne 3
");

// OU en un seul printf :
printf("Ligne 1
Ligne 2
Ligne 3
");

printf("  *  \n");
printf(" *** \n");
printf("*****\n");

// Affiche un triangle :
//   *
//  ***
// *****

