steps.codeExample:
// ❌ DANGER : variable non initialisée
int x;
printf("%d", x);  // Affiche n'importe quoi !

// ✅ BON : variable initialisée
int x = 0;
printf("%d", x);  // Affiche 0

int a = 10;
int b = 3;

int somme = a + b;       // 13
int produit = a * b;     // 30
int quotient = a / b;    // 3 (division entière !)
int reste = a % b;       // 1 (10 divisé par 3 = reste 1)

codeExamples.code:
int age = 19;
printf("J'ai %d ans\n", age);
// Affiche : J'ai 19 ans

int a = 42;
int b = 21;
printf("%d + %d = %d\n", a, b, a + b);
// Affiche : 42 + 21 = 63

int a = 10;
int b = 3;
int quotient = a / b;     // quotient = 3
int reste = a % b;        // reste = 1

int a = 5;
int b = 2;

float resultat1 = a / b;           // 2.0 (division entière !)
float resultat2 = (float)a / b;    // 2.5 (cast de a en float)

printf("%.1f\n", resultat1);  // 2.0
printf("%.1f\n", resultat2);  // 2.5

float pi = 3.14;
int entier = (int)pi;  // Troncature : 3

printf("%d\n", entier);  // 3

const int MAX_LEVEL = 10;
const float PI = 3.14;

printf("%d\n", MAX_LEVEL);  // OK : lecture

// MAX_LEVEL = 20;  // ❌ ERREUR : on ne peut pas modifier

int score = 100;          // Variable (modifiable)
const int MAX = 999;      // Constante (non modifiable)

score = 150;  // ✅ OK
// MAX = 1000;   // ❌ ERREUR de compilation

int note1 = 15;
int note2 = 18;
int note3 = 12;

int somme = note1 + note2 + note3;
float moyenne = (float)somme / 3;

printf("Moyenne : %.2f\n", moyenne);

int a = 5;
int b = 10;
int temp;

temp = a;  // Sauvegarde a
a = b;     // a prend la valeur de b
b = temp;  // b prend l'ancienne valeur de a

printf("a = %d, b = %d\n", a, b);  // a = 10, b = 5

