steps.codeExample:
int age = 19;
char initiale = 'M';

printf("Age: %d\n", age);           // Age: 19
printf("Initiale: %c\n", initiale); // Initiale: M

int nombre = 42;
char lettre = 'A';

printf("Mon nombre: %d\n", nombre);
printf("Mon caractère: %c\n", lettre);

// OU en un seul printf :
printf("Mon nombre: %d\nMon caractère: %c\n", nombre, lettre);

