printf("X = %d\nY = %d\n", x, y);

steps.codeExample:
char lettre = 'A';

printf("%c\n", lettre);  // Affiche : A
printf("%d\n", lettre);  // Affiche : 65 (code ASCII de 'A')

int code = 66;
printf("%c\n", code);    // Affiche : B
printf("%d\n", code);    // Affiche : 66

// ✅ BON
int nombre = 42;
printf("%d\n", nombre);

char lettre = 'A';
printf("%c\n", lettre);

// ❌ MAUVAIS
printf("%c\n", 1000);  // Affiche un caractère bizarre
printf("%d\n", "texte"); // ERREUR !

codeExamples.code:
int score = 85;
printf("Score: %d%%\n", score);
// Affiche : Score: 85%

char majuscule = 'A';
char minuscule = majuscule + 32;  // 'a'
printf("%c\n", minuscule);  // Affiche : a

char c = 'A';
c = c + 1;
printf("%c\n", c);  // Affiche : B

int x = 10;
int y = 20;
int z = 30;

printf("x=%d, y=%d, z=%d\n", x, y, z);
// Affiche : x=10, y=20, z=30

int age = 19;
char initiale = 'M';

printf("Age: %d, Initiale: %c\n", age, initiale);
// Affiche : Age: 19, Initiale: M

int score = 12500;
int level = 3;

printf("Score: %d | Level: %d\n", score, level);

char lettre = 'A';

printf("Lettre: %c, Code ASCII: %d\n", lettre, lettre);
// Affiche : Lettre: A, Code ASCII: 65

