// Bubble sort : O(n^2) en moyenne et pire
// Insertion sort : O(n^2) en moyenne, O(n) au meilleur
// Quicksort : O(n log n) en moyenne, O(n^2) au pire
// Mergesort : O(n log n) toujours

void partition(int *arr, int lo, int hi)
{
	int pivot = arr[hi];
	int i = lo - 1;
	for (int j = lo; j < hi; j++)
		if (arr[j] <= pivot)
			swap(&arr[++i], &arr[j]);
	swap(&arr[i + 1], &arr[hi]);
}

void merge(int *arr, int *tmp, int lo, int mid, int hi)
{
	int i = lo, j = mid + 1, k = lo;
	while (i <= mid && j <= hi)
	{
		if (arr[i] <= arr[j])
			tmp[k++] = arr[i++];
		else
			tmp[k++] = arr[j++];
	}
	while (i <= mid) tmp[k++] = arr[i++];
	while (j <= hi) tmp[k++] = arr[j++];
}

